INSERT INTO `admin_table` (`id`, `name`, `username`, `password`, `role`, `role_id`, `email`, `phone`, `bio`, `somthing_about`, `photo`, `create_at`) VALUES
(1, 'Amin IT', 'aminit', 'aminit1234', 'SuperAdmin', 1, 'info@aminitltd.com', '+880 1888071000', 'I love Amin IT', '<div class=\"elementor-column elementor-col-50 elementor-top-column elementor-element elementor-element-e2a7e7d compress-right-side\" data-id=\"e2a7e7d\" data-element_type=\"column\">\n			<div class=\"elementor-widget-wrap elementor-element-populated\">\n								<div class=\"elementor-element elementor-element-b65caef elementor-widget elementor-widget-rt-text-with-button\" data-id=\"b65caef\" data-element_type=\"widget\" data-widget_type=\"rt-text-with-button.default\">\n				<div class=\"elementor-widget-container\">\n			<div class=\"no-animation\">\n	<div class=\"section-title-holder no-animation\">\n		<div class=\"title-text-button title-text-style1\">		\n									<div class=\"translate-bottom-75 opacity-animation transition-150 transition-delay-500\">\n				<h2 class=\"rtin-title\">Exclusive Agency For Technology Provide Soluation</h2>\n			</div>\n									<div class=\"translate-bottom-75 opacity-animation transition-150 transition-delay-1000\">\n				<div class=\"rtin-content\"><p><span style=\"font-weight: 400\">Amin It \nLtd. is one of the leading IT firms in Bangladesh, providing various \nIT-related services like Website design &amp; Development, Graphic \nDesign, Animations, Software Services, UI/UX Design, Apps Development, \nE-Commerce Solution, &amp; Consultancy Services with a good reputation \nfor a long time.</span></p><p><span style=\"font-weight: 400\">In the last\n few years, Amin IT Ltd. has grown from a small software business in \nDhaka City with a few employees to now a big family of over 30+.</span></p><p><span style=\"font-weight: 400\">We\n think every business is different and their services are different too.\n For this, we have built such services that can fulfil all your \nrequirements. We help companies, manufacturers, and agencies to smooth \nand magnify their work operations through shiny design and expertly \ndeveloped web applications, system solutions, graphical content, and \nproductive support.</span></p><p>				\n				</p><div class=\"popup-img\">\n					<div class=\"item-icon\">\n						<a class=\"rtin-play rt-video-popup\" href=\"https://www.youtube.com/watch?v=1iIZeIy7TqM\"><i class=\"fas fa-play\"></i></a>\n											</div>\n				</div>\n			\n		\n	\n		\n				\n					\n		\n				<div class=\"elementor-column elementor-col-50 elementor-top-column elementor-element elementor-element-91e1459 compress-right-side\" data-id=\"91e1459\" data-element_type=\"column\" data-settings=\"{&quot;background_background&quot;:&quot;gradient&quot;}\">\n			<div class=\"elementor-widget-wrap elementor-element-populated\">\n								<div class=\"elementor-element elementor-element-c2854ee elementor-widget elementor-widget-rt-text-with-button\" data-id=\"c2854ee\" data-element_type=\"widget\" data-widget_type=\"rt-text-with-button.default\">\n				<div class=\"elementor-widget-container\">\n			<div class=\"no-animation\">\n	<div class=\"section-title-holder no-animation\">\n		<div class=\"title-text-button title-text-style1\">		\n									<div class=\"translate-bottom-75 opacity-animation transition-150 transition-delay-500\">\n				<h2 class=\"rtin-title\">Why do you choose us?</h2>\n			</div>\n									<div class=\"translate-bottom-75 opacity-animation transition-150 transition-delay-1000\">\n				<div class=\"rtin-content\"><p><span style=\"font-weight: 400\">Our web \ndesign and development team has strong technical and analytical skills. \nThey are also experts in software languages, platforms and modern \nmethods. We work for customer satisfaction. We understand user needs, \ndevelop software solutions, monitor performance and modify programs as \nneeded. </span></p><p><span style=\"font-weight: 400\">\"Customer Satisfaction is our Gratification\"</span></p><ul class=\"dubble-list\"><li>Web Design &amp; Development</li><li>Best Leadership Ideas</li><li>Offerdable Price</li><li>Pixel Perfect</li><li>Online Support</li><li>Expert Team</li><li>Quick Access</li><li>Creativity and Clarity.</li></ul></div>\n			</div>\n					\n					</div>\n	</div>\n</div>		</div>\n				</div>\n					</div>\n		</div><p><span style=\"font-weight: 400\"></span></p></div>\n			</div>\n					\n					</div>\n	</div>\n</div>		</div>\n				</div>\n					</div>\n		</div>', 'upload/admin/admin1.jpg', '2022-04-17 04:04:56'),
(11, 'Johan Editor', 'editor', '1234', 'Editor', 18, 'editor@gmail.com', NULL, NULL, NULL, NULL, '2022-04-20 09:41:57'),
(12, 'Jovan Admin', 'admin', '1234', 'Admin', 20, 'admin@gmail.com', '+880 1733061986', 'Hello i am admin bio', '<div class=\"elementor-column elementor-col-33 elementor-top-column elementor-element elementor-element-aa96641\" data-id=\"aa96641\" data-element_type=\"column\">\n			<div class=\"elementor-widget-wrap elementor-element-populated\">\n								<div class=\"elementor-element elementor-element-439e7f6 elementor-widget elementor-widget-rt-contact-address\" data-id=\"439e7f6\" data-element_type=\"widget\" data-widget_type=\"rt-contact-address.default\">\n				<div class=\"elementor-widget-container\">\n			\n<div class=\"has-animation active-animation\">\n	<div class=\"rtin-address-default light\">\n		<div class=\"rtin-address-info\">\n										<div class=\"rtin-address\">\n											<div class=\"rtin-item translate-left-75 opacity-animation transition-150 transition-delay-100\">\n															<div class=\"rtin-icon\"><i class=\"flaticon-mail\"></i></div>\n								<div class=\"rtin-info\"><h3>Email &amp; Phone</h3>info@aminitltd.com<br><a href=\"tel:+880 1888071000\">+880 1888071000</a> </div>\n													</div>\n											<div class=\"rtin-item translate-left-75 opacity-animation transition-150 transition-delay-600\">\n															<div class=\"rtin-icon\"><i class=\"flaticon-placeholder\"></i></div>\n								<div class=\"rtin-info\"><h3>Our Location</h3>Kha-53/4, Road No-4, Nikunja-2, Tanpara, Khilkhet, Dhaka-1229.</div>\n													</div>\n											<div class=\"rtin-item translate-left-75 opacity-animation transition-150 transition-delay-1100\">\n															<div class=\"rtin-icon\"><i class=\"flaticon-share\"></i></div>\n								<div class=\"rtin-info\"><h3>Get In Touch</h3><a target=\"_blank\" href=\"https://facebook.com/aminitltd\">Facebook</a> </div>\n													</div>\n									</div>\n					</div>\n	</div>\n</div>		</div>\n				</div>\n					</div>\n		</div>\n				<div class=\"elementor-column elementor-col-66 elementor-top-column elementor-element elementor-element-68752e6\" data-id=\"68752e6\" data-element_type=\"column\">\n			<div class=\"elementor-widget-wrap elementor-element-populated\">\n								<div class=\"elementor-element elementor-element-cad09e1 elementor-widget elementor-widget-shortcode\" data-id=\"cad09e1\" data-element_type=\"widget\" data-widget_type=\"shortcode.default\">\n				<div class=\"elementor-widget-container\">\n					<div class=\"elementor-shortcode\"><div role=\"form\" class=\"wpcf7\" id=\"wpcf7-f1680-p11-o1\" dir=\"ltr\" lang=\"en-US\">\n<div class=\"screen-reader-response\"><p role=\"status\" aria-live=\"polite\" aria-atomic=\"true\"></p> </div>\n<form action=\"/contact/#wpcf7-f1680-p11-o1\" method=\"post\" class=\"wpcf7-form init\" novalidate=\"novalidate\" data-status=\"init\">\n\n<div class=\"contact-form\">\n<div class=\"row gutters-10\">\n<div class=\"col-sm-6\">\n<div class=\"form-group\"><span class=\"wpcf7-form-control-wrap text-665\"><input type=\"text\" name=\"text-665\" value=\"\" size=\"40\" class=\"wpcf7-form-control wpcf7-text wpcf7-validates-as-required form-control\" aria-required=\"true\" aria-invalid=\"false\" placeholder=\"Name*\"></span></div>\n</div>\n<div class=\"col-sm-6\">\n<div class=\"form-group\"><span class=\"wpcf7-form-control-wrap email-706\"><input type=\"email\" name=\"email-706\" value=\"\" size=\"40\" class=\"wpcf7-form-control wpcf7-text wpcf7-email wpcf7-validates-as-required wpcf7-validates-as-email form-control\" aria-required=\"true\" aria-invalid=\"false\" placeholder=\"E-mail Address*\"></span></div>\n</div>\n<div class=\"col-sm-12\">\n<div class=\"form-group\"><span class=\"wpcf7-form-control-wrap text-666\"><input type=\"text\" name=\"text-666\" value=\"\" size=\"40\" class=\"wpcf7-form-control wpcf7-text wpcf7-validates-as-required form-control\" aria-required=\"true\" aria-invalid=\"false\" placeholder=\"Subject*\"></span></div>\n</div>\n<div class=\"col-sm-12\">\n<div class=\"form-group\"><span class=\"wpcf7-form-control-wrap